import{_ as e,l as a,m as s,S as n}from"./chunks/framework.B9qehBrd.js";const l=JSON.parse('{"title":"安卓web调试","description":"","frontmatter":{},"headers":[],"relativePath":"src/前端笔记/其他/安卓web调试.md","filePath":"src/前端笔记/其他/安卓web调试.md","lastUpdated":1717660699000}');const i=e({name:"src/前端笔记/其他/安卓web调试.md"},[["render",function(e,l,i,r,p,t){return s(),a("div",null,[...l[0]||(l[0]=[n('<h1 id="安卓web调试" tabindex="-1">安卓web调试 <a class="header-anchor" href="#安卓web调试" aria-label="Permalink to &quot;安卓web调试&quot;">​</a></h1><h2 id="在安卓里调试网页的最新方案" tabindex="-1">在安卓里调试网页的最新方案 <a class="header-anchor" href="#在安卓里调试网页的最新方案" aria-label="Permalink to &quot;在安卓里调试网页的最新方案&quot;">​</a></h2><ul><li>可调试范围 <code>微信webview</code>、<code>QQwebview</code>、<code>手机自带浏览器</code></li></ul><p>微信调试需执行第一步，其他跳过。</p><ol><li><p>第一步 在微信里打开链接： <a href="http://debugxweb.qq.com/?inspector=true" target="_blank" rel="noreferrer">http://debugxweb.qq.com/?inspector=true</a></p></li><li><p>第二步 打开安卓手机的开发者权限 一般都在设置里，找到关于本机，然后多次点击手机的版本号，就会出现开启调试选项的对话框。开启后，在设置中找到开发者选项，开启调试；具体怎么做，每个手机大同小异。</p></li><li><p>第三步 将手机通过数据线连接电脑，这时如果是第一次连接电脑，会弹出是否信任调试的对话框，选择信任</p></li><li><p>第四步 打开谷歌浏览器 或则Edge浏览器都可以，在浏览器地址栏输入:</p></li></ol><p>推荐使用chrome浏览器，谷歌浏览器在这一步可能会显示空白，拿不到手机数据的，需要翻墙下载基础包。</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span># 如果是edge浏览器：</span></span>\n<span class="line"><span>edge://inspect/#device</span></span>\n<span class="line"><span></span></span>\n<span class="line"><span># 如果是谷歌Chrome浏览器：</span></span>\n<span class="line"><span>chrome://inspect/#device</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br></div></div><h2 id="开始调试" tabindex="-1">开始调试 <a class="header-anchor" href="#开始调试" aria-label="Permalink to &quot;开始调试&quot;">​</a></h2><ul><li>在手机微信里打开网页，</li><li>在电脑上刚打开的浏览器里的devices列表里就能看到可调试设备下的网页地址。</li><li>点击页面标题 就可以开始正常调试了。</li><li>同理，也可以调试qq上的网页； 也可以正常调试移动设备上的浏览器网页（前提是手机自带浏览器）</li></ul>',9)])])}]]);export{l as __pageData,i as default};
